mapper.select:
select admin_number,admin_id, admin_name, admin_date 
from tbl_admin
where admin_number = #{adminNumber};

select u.user_number , g.grade_name , u.user_nickname , u.user_date from tbl_grade g join tbl_user u on g.grade_number = u.grade_number where 1 = 1
AND u.user_nickname like concat('%',#{searchWord},'%')
order by u.user_number desc limit #{startRow}, #{rowCount}

select u.user_number, g.gosu_number, u.user_nickname, count(distinct q.question_number) as question_count, count(distinct case when q.question_status = 1 then q.question_number end) as answer_count, group_concat(distinct f.field_name separator ', ') as field_names, ( select count(qr.question_number) from tbl_question qr where qr.gosu_number = g.gosu_number and qr.question_status = 1 ) as question_reply_count from tbl_gosu g join tbl_gosu_field gf on g.gosu_number = gf.gosu_number join tbl_field f on gf.field_number = f.field_number join tbl_user u on g.user_number = u.user_number left outer join tbl_question q on g.gosu_number = q.gosu_number where grade_number = 500
AND u.user_nickname like concat('%',#{searchWord},'%')
group by g.gosu_number limit #{startRow}, #{rowCount}

mapper.update:
update tbl_user set grade_number =
500
where user_number = #{userNumber}

update tbl_user set
grade_number = 100
where user_number = #{userNumber}
